function onLoad() {
    //Type appropriate comment here, and begin script below
    g_form.setReadOnly('applicant', true);
    g_form.setReadOnly('applicant.first_name', true);
    g_form.setReadOnly('applicant.last_name', true);
    g_form.setReadOnly('applicant.email', true);
    g_form.setReadOnly('applicant.mobile_phone', true);
    g_form.setReadOnly('applicant.skills', true);
    g_form.setReadOnly('applicant.last_working_date', true);
    g_form.setReadOnly('applicant.expected_ctc', true);
    g_form.setReadOnly('applicant.experience', true);
    g_form.setReadOnly('applicant.linkedin_profile', true);
	g_form.setDisplay('applying_for',false);


